mapper.select:
	select * from (
    select rownum rnum , data.* from (
    select num , pwd , email , m_text , to_char(reg_date , 'YYYY.MM.DD') reg_date 
    from board
    where ${searchKey} like  '%' || #{searchValue} ||'%'
    order by reg_date desc
    )data
	)
		where rnum >= #{start} and rnum<= #{end}

select num , pwd , email , m_text , to_char(reg_date , 'YYYY.MM.DD') reg_date 
from board
where num = #{num}
